cy.request({
  method: 'POST',
  url: 'https://simple-books-api.glitch.me/api-clients/',
  failOnStatusCode: false,
  body: {
    clientName: "ondra",
    clientEmail: "me@mail.com"
  }
}).as('authRequest');
cy.get('@authRequest').then(response => {
  expect(response.status).to.be.equal(409);
  cy.wrap(response.body).should('deep.include', {
    error: 'API client already registered. Try a different email.'
  });
});

cy.request({
  method: 'GET',
  url: 'https://simple-books-api.glitch.me/books',
  qs: {
    type: 'non-fiction'
  }
}).as('nonFiction');
cy.get('@nonFiction').then(response => {
  expect(response.status).to.be.equal(200);
  assert.isArray(response.body);
  expect(response.body).have.length(2);
  cy.wrap(response.body[0]).should('deep.include', {
    id: 2,
    name: "Just as I Am",
    type: "non-fiction",
    available: false
  });
  cy.wrap(response.body[1]).should('contain', {
    available: true
  });
});

cy.request({
  method: 'GET',
  url: `https://simple-books-api.glitch.me/books/${bookId}`
}).as('singleBook');
cy.get('@singleBook').then(response => {
  expect(response.status).to.be.equal(200);
  cy.wrap(response.body).should('deep.include', {
    "id": 5,
    "name": "Untamed",
    "author": "Glennon Doyle",
    "type": "non-fiction",
    "price": 15.5,
    "current-stock": 20,
    "available": true
  });
});

cy.request({
  method: 'POST',
  url: `https://simple-books-api.glitch.me/orders`,
  auth: {
    bearer: authToken
  },
  body: {
    bookId: bookId,
    clientName: "Valentin"
  }
}).as('order');
cy.get('@order').then(response => {
  expect(response.status).to.be.equal(201);
  expect(response.body.created).to.be.true;
  expect(response.body).to.include.key('orderId');
  orderId = response.body.orderId;
});

cy.request({
  method: 'GET',
  url: `https://simple-books-api.glitch.me/orders`,
  auth: {
    bearer: authToken
  }
}).as('order');
cy.get('@order').then(response => {
  expect(response.status).to.be.equal(200);
  expect(response.body).to.have.length(1);
  expect(response.body[0]).to.include.key('id', 'quantity');
  expect(response.body[0].id).to.equal(orderId);
  expect(response.body[0].quantity).to.equal(1);
});

cy.request({
  method: 'PATCH',
  url: `https://simple-books-api.glitch.me/orders/${orderId}`,
  auth: {
    bearer: authToken
  },
  body: {
    customerName: 'Val'
  }
}).as('order');
cy.get('@order').then(response => {
  expect(response.status).to.be.equal(204);
});
cy.request({
  method: 'GET',
  url: `https://simple-books-api.glitch.me/orders`,
  auth: {
    bearer: authToken
  }
}).as('order');
cy.get('@order').then(response => {
  expect(response.status).to.be.equal(200);
  expect(response.body[0]).to.include.key('customerName');
  expect(response.body[0].customerName).to.eql('Val');
});

cy.request({
  method: 'DELETE',
  url: `https://simple-books-api.glitch.me/orders/${orderId}`,
  auth: {
    bearer: authToken
  }
}).as('delOrder');
cy.get('@delOrder').then(response => {
  expect(response.status).to.be.equal(204);
});
cy.request({
  method: 'GET',
  url: `https://simple-books-api.glitch.me/orders`,
  auth: {
    bearer: authToken
  }
}).as('order');
cy.get('@order').then(response => {
  expect(response.status).to.be.equal(200);
  expect(response.body).to.have.length(0);
});
